mapper.update:
UPDATE tb_mbr
SET mbr_ncnm = #{mbr_ncnm},
    mbr_password = #{mbr_password},
    mbr_tel = #{mbr_tel},
    mbr_email = #{mbr_email}
WHERE mbr_sn = #{mbr_sn}

UPDATE tb_mbr
SET use_yn = 'N'
WHERE mbr_sn = #{mbr_sn}

mapper.select:
SELECT COUNT(*)
FROM tb_mbr
WHERE mbr_id= #{mbr_id}

mapper.update:
UPDATE tb_mbr
SET FAIL_CNT = #{fail_cnt},
    LOCK_YN = #{lock_yn}
WHERE MBR_ID = #{mbr_id}
